<html lang="es"><head><meta charset="UTF-8"><meta content="IE=edge"http-equiv="X-UA-Compatible"><meta content="width=device-width,initial-scale=1"name="viewport"><link href="IndexStyles.css"rel="stylesheet"><title>RSS_Reader</title></head><body><div id="headContainer"><div id="headTitle"><a href="Index.php"id="linkTitle">RSS Reader</a></div><div id="browser"><input placeholder="News"id="newsBox"> <button onclick="searchWord()"id="searchButton">SEARCH</button></div></div><div id="container"><div id="ContenedorCRUD"><div id="EncabezadoCRUD"><form action="php/SaveNews.php"method="POST"><input class="linkBox"id="NombreURL"name="NombreURL"autocomplete="off"placeholder="RSS Name"> <input class="linkBox"id="CadenaURL"name="CadenaURL"autocomplete="off"placeholder="RSS Link"> <input class="cyanButton"class="cyanButton"id="addButton"name="btn_Agregar_CRUD"type="submit"value="ADD+"></form></div><div id="ContenidoCRUD"><div id="TituloCrud"><p class="adli">Added Links</p></div><?php include './Funciones.php';$links=getLinks();foreach($links as $product){ ?><div class="product"><div class="left"><p class="linkNameText"><?php echo $product['NombreLink'] ?></p></div></div><?php } ?></div></div><div id="newsContainer"><div id="headNewsCont"><div id="chooseSort"><p class="sortByText">SORTING BY:</p><p class="sortByText"id="currentSort">Date</p><div class="dropdown-sort"><button onclick="SortingBy()"class="dropbtn"><i class="fa fa-chevron-down"></i></button><div id="sortings"class="dropdown-sort-content"><button onclick="byDate()"class="sortbtn">Date</button> <button onclick="byTitle()"class="sortbtn">Title</button> <button onclick="byDescription()"class="sortbtn">Description</button> <button onclick="byCategory()"class="sortbtn">Category</button></div></div></div><button onclick="loadNews()"class="cyanButton"id="updatebtn">UPDATE</button></div><div id="readerContainer"><div id="sample"></div></div><template id="template-card"><div class="news-card"><div id="n-text"><div id="s-title"></div><div id="s-cat"></div><div id="s-desc"></div><div id="s-img"><img alt=""class="card-img"src=""></div><div id="s-url"></div><div id="s-date"></div></div></div></template></div></div><script src="indexMethods.js?V=1.0.3"></script></body></html>