<?php include_once('./php/util/mysql_handler.php');function getLinks(){$handler=new MySqlHandler();$mysql=$handler->connection();$mysql->begin_transaction();$links=[];try{$links_query=$mysql->prepare("SELECT * FROM links ORDER BY RAND()");$links_query->execute();$result=$links_query->get_result();while($registro=$result->fetch_assoc()){$links[]=$registro;}$mysql->commit();}catch(mysqli_sql_exception $exception){$db->rollback();echo $exception;}return $links;}function transformLinksJson(){$arraylinks=getURLLinks();$linksEncodedJson=json_encode($arraylinks);echo $linksEncodedJson;return $linksEncodedJson;}function getURLLinks(){$handler=new MySqlHandler();$mysql=$handler->connection();$resultado=$mysql->query("SELECT Link FROM links");while($registro=$resultado->fetch_assoc()){$urllinks[]=$registro;}return $urllinks;} ?>